import React from "react";
import Header from "../common/Header";

const DashboardHeader = () => {
  return (
    <div className="text-lg font-medium">Dashboard</div>
  );
};

const AnnouncementsHeader = ({ onCreateAnnouncement }) => {
  return (
    <div className="flex items-center justify-between">
      <div className="text-lg font-medium">Announcements</div>
      {onCreateAnnouncement && (
        <button className="btn btn-primary">Create Announcement</button>
      )}
    </div>
  );
};

const Announcement = ({ message, postedDate, author }) => {
  return (
    <div className="p-4 border-b">
      <p className="text-base">{message}</p>
      <div className="flex items-center mt-2 text-sm">
        <span className="mr-2">Posted on {postedDate}</span>
        <span>by {author}</span>
      </div>
    </div>
  );
};

const AnnouncementList = () => {
  const [announcements, setAnnouncements] = useState([]);

  useEffect(() => {
    // Simulate fetching announcements from an API
    const fetchData = async () => {
      const response = await fetch('/api/announcements');
      const data = await response.json();
      setAnnouncements(data);
    };
    fetchData();
  }, []);

  return (
    <div>
      {announcements.map((announcement) => (
        <Announcement
          key={announcement.id}
          message={announcement.message}
          postedDate={announcement.postedDate}
          author={announcement.author}
        />
      ))}
    </div>
  );
};

const HomePage = () => {
  return (
    <>
      <div className="flex flex-col p-6">
        <DashboardHeader />
        <AnnouncementsHeader onCreateAnnouncement={() => {}} />
        <AnnouncementList />
      </div>
    </>
  );
};

export default HomePage;
